"use strict";app.controller("ContactsDelete",function($rootScope,$scope,$state,$timeout,$http,utils,modal){var container=$("#dialog_container");$scope.viewData={},$scope.viewData.name=$scope.targetDepartmentName;var doIt=function(){$http({url:app.url.yiliao.deleteByDepart,method:"post",data:{access_token:app.url.access_token,ids:$scope.targetDepartmentId}}).then(function(resp){1===resp.data.resultCode?$state.go("app.contacts.list",{},{reload:!0}):(console.warn("删除失败！"),modal.toast.error(resp.data.resultMsg))},function(x){console.error(x.statusText)})};$scope["do"]=function(){doIt()},$scope.cancel=function(){container.prev().remove(),container.remove(),window.history.back()}});